import { createApp } from './app.js';

export default (context) => {
  // 因为有可能会是异步路由钩子函数或组件，所以我们将返回一个 Promise，
  // 以便服务器能够等待所有的内容在渲染前，
  // 就已经准备就绪。
  return new Promise((resolve, reject) => {
    const { app, store, router } = createApp();

    // 设置服务器端 router 的位置
    router.push(context.url);

    // 等到 router 将可能的异步组件和钩子函数解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents();

      console.log('context.url', context.url);
      console.log('matchedComponents', JSON.stringify(matchedComponents));

      // 匹配不到的路由，执行 reject 函数，并返回 404
      if (!matchedComponents.length) {
        return reject({ code: 404 });
      }

      Promise.all(
        matchedComponents.map((component) => {
          if (component.asyncData) {
            return component.asyncData({ store });
          }
        })
      ).then(() => {
        // 当使用 template 时，context.state 将作为 window.__INITIAL_STATE__ 状态，自动嵌入到最终的 HTML 中
        context.state = store.state;

        // Promise 应该 resolve 应用程序实例，以便它可以渲染
        resolve(app);
      });
    }, reject);
  });
};

import Vue from 'vue';
import Router from 'vue-router';
import Bar from '../components/Bar.vue';

Vue.use(Router);

function createRouter() {
  const routes = [
    {
      path: '/index',
      component: () => import('@/components/Bar.vue'), // 异步路由,
    },
    {
      path: '/foo',
      component: () => import('@/components/Foo.vue'), // 异步路由
    },
  ];

  const router = new Router({
    mode: 'history',
    routes,
  });
  console.log(router, router.getMatchedComponents());
  return router;
}

export default createRouter;

import Vue from 'vue';
import Vuex from 'vuex';
import * as api from '@/api/news.js';
Vue.use(Vuex);

function createStore() {
  const store = new Vuex.Store({
    state: {
      bar: '',
    },
    mutations: {
      SET_BAR(state, data) {
        state.bar = data;
      },
    },

    actions: {
      fetchBar({ commit }) {
        return api
          .getTopics()
          .then(({ data }) => {
            console.log('data---', data);
            commit('SET_BAR', JSON.stringify(data[0]));
          })
          .catch((err) => {
            console.error(err);
          });
      },
    },
  });

  if (typeof window !== 'undefined' && window.__INITIAL_STATE__) {
    // console.log('window.__INITIAL_STATE__', window.__INITIAL_STATE__);
    store.replaceState(window.__INITIAL_STATE__);
  } else {
    console.log('no browser');
  }

  return store;
}

export default createStore;

